import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';                     // lowercase 'n'
import WebcamExpression from './webcamExpression'; // Capital W and E (matches your file)
import PlaylistDisplay from './playlist1';   // Capital P and D (matches your file)

function Home({ setAuth }) {
  const [user, setUser] = useState(null);
  const [selectedLanguages, setSelectedLanguages] = useState(['English']);
  const [detectedEmotion, setDetectedEmotion] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);
      setSelectedLanguages(userData.languages || ['English']);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setAuth(false);
    navigate('/login');
  };

  const handleLanguageChange = (languages) => {
    setSelectedLanguages(languages);
  };

  return (
    <div>
      <Navbar 
        user={user} 
        onLogout={handleLogout} 
        onLanguageChange={handleLanguageChange}
        selectedLanguages={selectedLanguages}
      />
      <div style={{ padding: '20px' }}>
        <h2>Welcome, {user?.name}!</h2>
        
        <div style={{ marginTop: '20px' }}>
          <h3>Scan Your Face Expression</h3>
          <WebcamExpression onEmotionDetected={setDetectedEmotion} />
          {detectedEmotion && (
            <p style={{ marginTop: '10px', fontSize: '18px', fontWeight: 'bold' }}>
              Detected Emotion: {detectedEmotion}
            </p>
          )}
        </div>

        <div style={{ marginTop: '30px' }}>
          <PlaylistDisplay 
            emotion={detectedEmotion} 
            languages={selectedLanguages}
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
